import React, { useContext, useState } from 'react';
import { Table, Button } from 'reactstrap';
import { UserContext } from '../App';
import FileViewerButton from './FileViewerButton';
import { fetchAndSetFileHighlights } from "../utils/setPdfHighlights";
import { BASE_URL_DEV } from "../utils";

function FileMetadataViewer({ files, sideItemsClick }) {
    const { state, dispatch } = useContext(UserContext);
    const [justFilename, setJustFilename] = useState('');

    const handleInspect = async (file) => {
        console.log("Inspecting file:", file);

        await fetchAndSetFileHighlights(dispatch, state, file, () => {});
        console.log("Finished fetching and setting highlights for file:", file);

        const updatedFile = {
            ...file,
            url: `${BASE_URL_DEV}/uploads/${state.auth.userPublicId}/${file.name}`
        };

        const filename = file.name;
        setJustFilename(filename);
        dispatch({ type: 'SET_CURR_FILE', payload: filename });
        console.log("Dispatched SET_CURR_FILE with payload:", filename);
    };

    return (
        <div className="file-metadata-viewer">
            <h2>Uploaded Files</h2>
            <Table responsive bordered className="custom-table">
                <thead>
                    <tr>
                        <th>File Name</th>
                        <th>File Size</th>
                        <th>Upload Date</th>
                        <th>Category</th>
                        <th>Summary</th>
                        <th>Inspect</th>
                        <th>PDF View</th>
                    </tr>
                </thead>
                <tbody>
                    {files.map((file, index) => (
                        <tr key={index}>
                            <td>{file.name}</td>
                            <td>{(file.size / 1024).toFixed(2)} KB</td>
                            <td>{new Date().toLocaleDateString()}</td>
                            <td>{file.category}</td>
                            <td>{file.summary}</td>
                            <td>
                                <Button color="primary" onClick={() => handleInspect(file)}>Inspect</Button>
                            </td>
                            <td>
                                <FileViewerButton file={justFilename} sideItemsClick={sideItemsClick} />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
}

export default FileMetadataViewer;
