import React, { useState } from 'react';
import '../style/theme-styles/components/Chatstles.css'; // Assuming you save your CSS here


const ChatUI = () => {
    const [messages, setMessages] = useState([
        { id: 1, avatarUrl: 'https://randomuser.me/api/portraits/women/44.jpg', nickName: 'Monika Figi', message: 'Somewhere stored deep, deep in my memory banks is the phrase "It really whips the llama\'s ass".' },
        { id: 2, avatarUrl: 'https://randomuser.me/api/portraits/men/32.jpg', nickName: 'Thomas Rogh', message: 'Think the guy that did the voice has a Twitter?' },
        { id: 3, avatarUrl: 'https://randomuser.me/api/portraits/women/44.jpg', nickName: 'Monika Figi', message: 'WE MUST FIND HIM!!' },
        { id: 4, avatarUrl: 'https://randomuser.me/api/portraits/men/9.jpg', nickName: 'Dennis Mikle', message: 'Winamp\'s still an essential.', type: 'me' }
    ]);

    const addMessage = text => {
        setMessages([...messages, { id: messages.length + 1, avatarUrl: 'https://randomuser.me/api/portraits/men/9.jpg', nickName: 'Dennis Mikle', message: text, type: 'me' }]);
    };

    return (
        <div id="chat" className="--dark-theme">
            <div className="chat__conversation-board">
                {messages.map(msg => (
                    <div key={msg.id} className={`chat__conversation-board__message-container ${msg.type === 'me' ? 'reversed' : ''}`}>
                        <div className="chat__conversation-board__message__person">
                            <div className="chat__conversation-board__message__person__avatar">
                                <img src={msg.avatarUrl} alt={msg.nickName} />
                            </div>
                            <span className="chat__conversation-board__message__person__nickname">{msg.nickName}</span>
                        </div>
                        <div className="chat__conversation-board__message__context">
                            <div className="chat__conversation-board__message__bubble">
                                <span>{msg.message}</span>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <div className="chat__conversation-panel">
                <div className="chat__conversation-panel__container">
                    <input
                        className="chat__conversation-panel__input panel-item"
                        placeholder="Type a message..."
                        onKeyDown={e => { if (e.key === 'Enter') addMessage(e.target.value); }}
                    />
                    <button className="chat__conversation-panel__button panel-item btn-icon send-message-button"
                        onClick={() => addMessage('Random message')}>
                        Send
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ChatUI;
