import React, { useState } from 'react';
import '../style/theme-styles/components/Chatstyles.css';

const ChatUI = () => {
    const [messages, setMessages] = useState([
        { id: 1, nickName: 'Yahya', message: 'Hello there!', type: 'other' }, // Changed type to 'other' for clarity
    ]);

    const sendMessage = (text) => {
        if (text.trim()) {
            const newMessage = {
                id: messages.length + 1,
                nickName: 'Casebook AI',
                message: text,
                type: 'me'  // Ensure this type is used for "me" messages
            };
            setMessages([...messages, newMessage]);
        }
    };
    const longMessage = `This is being recognised. As Jamie Susskind observes in his book Future Politics, progress is being made toward developing principles of algorithmic audit. On 12 February this year the European Parliament adopted a resolution declaring that "algorithms in decision-making systems should not be deployed without a prior algorithmic impact assessment."`;

    return (
        <div id="chat" className="--dark-theme">
            <div className="chat__conversation-board">
                {messages.map((msg, index) => (
                    <div key={index} className={`chat__conversation-board__message-container ${msg.type === 'me' ? 'me' : ''}`}>
                        <div className="chat__conversation-board__message__person">
                            {msg.type === 'me' ?
                                <div style={{ width: '35px', height: '35px', borderRadius: '50%', backgroundColor: 'blue', color: 'white', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                                    Y
                                </div> :
                                <img src="/static/media/scaletransm2.5eb52ef8.png" alt={msg.nickName} style={{ width: '35px', height: '35px', borderRadius: '50%' }} />
                            }
                            <span className="chat__conversation-board__message__person__nickname">{msg.nickName}</span>
                        </div>
                        <div className="chat__conversation-board__message__context">
                            <span className="chat__conversation-board__message__bubble">{msg.message}</span>
                        </div>
                    </div>
                ))}
            </div>
            <div className="chat__conversation-panel">
                <input
                    className="chat__conversation-panel__input"
                    placeholder="Type a message..."
                    onKeyDown={(e) => e.key === 'Enter' && sendMessage(e.target.value)}
                />
                <button className="chat__conversation-panel__button" onClick={() => sendMessage(longMessage)}>
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M2 21l21-9L2 3v7l15 2-15 2v7z" fill="currentColor"/>
                    </svg>
                </button>
            </div>
        </div>
    );
};

export default ChatUI;
