import React, { useContext } from 'react';
import { Table, Button } from 'reactstrap';
import { UserContext } from '../App';
import { showFileViewer } from '../shared/SidebarItemData/data';
import { fetchAndSetFileHighlights } from "../utils/setPdfHighlights";
import { BASE_URL_DEV } from "../utils";

function FileMetadataViewer({ files, sideItemsClick }) {
    const { state, dispatch } = useContext(UserContext);

    const handleInspect = async (file) => {
        const fileTypeMapping = {
            'pdf': showFileViewer,
            'txt': showFileViewer,
            'docx': showFileViewer
        };

        const fileExtension = file.name.split('.').pop();
        const viewer = fileTypeMapping[fileExtension];

        if (viewer) {
            // Fetch and set highlights for the current file
            await fetchAndSetFileHighlights(dispatch, state, [file], () => {});

            // Dispatch the current file and toggle the viewer
            const updatedFile = {
                ...file,
                url: `${BASE_URL_DEV}/uploads/${state.auth.userPublicId}/${file.name}`
            };
            dispatch({ type: 'SET_CURR_FILE', payload: updatedFile });

            // Ensure the viewer is set correctly
            sideItemsClick(showFileViewer);

            // Dispatch the viewer toggle
            dispatch({ type: 'TOGGLE_VIEWER', payload: viewer });
        } else {
            console.error('No viewer available for this file type');
        }
    };

    return (
        <div className="file-metadata-viewer">
            <h2>Uploaded Files</h2>
            <Table responsive bordered className="custom-table">
                <thead>
                    <tr>
                        <th>File Name</th>
                        <th>File Size</th>
                        <th>Upload Date</th>
                        <th>Category</th>
                        <th>Summary</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {files.map((file, index) => (
                        <tr key={index}>
                            <td>{file.name}</td>
                            <td>{(file.size / 1024).toFixed(2)} KB</td>
                            <td>{new Date().toLocaleDateString()}</td>
                            <td>{file.category}</td>
                            <td>{file.summary}</td>
                            <td>
                                <Button color="primary" onClick={() => handleInspect(file)}>Inspect</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
}

export default FileMetadataViewer;
