import React from 'react';

function StickyNote({ isScrolledTo, position, comment }) {
    // Calculate 30% of the viewport width
    const offset = window.innerWidth * 0.3;

    // Adjust the styling to include the calculated offset
    const style = {
        position: 'absolute',
        left: `${position.boundingRect.left - offset}px`,  // Subtract offset from the left position
        top: `${position.boundingRect.top}px`,
        background: 'yellow',
        padding: '10px',
        borderRadius: '5px',
        boxShadow: '0 2px 5px rgba(0,0,0,0.3)',
        zIndex: 1000  // Ensure it appears above other elements
    };

    // Ensure that the sticky note does not move out of the visible area on the left
    if (style.left < 0) {
        style.left = '10px';  // A little padding from the edge if it's too far left
    }

    return (
        <div style={style}>
            {comment && comment.text}
        </div>
    );
}

export default StickyNote;
