import React, { useState, useEffect } from 'react';
import '../style/theme-styles/components/Chatstyles.css';

const ChatUI = ({ socket }) => {
    const [messages, setMessages] = useState([
        { id: 1, nickName: 'Casebook AI', message: 'Hello there!', type: 'system' }
    ]);
    const [inputValue, setInputValue] = useState('');

    const sendMessage = (text, sender = 'Me') => {
        if (text.trim()) {
            const newMessage = {
                id: messages.length + 1,
                nickName: sender,
                message: text,
                type: sender === 'Me' ? 'me' : 'system'
            };
            setMessages([...messages, newMessage]);
            if (sender === 'Me') {
                socket.emit('openai-chat', { query: text });
            }
            setInputValue('');
        }
    };

    useEffect(() => {
        if (socket) {
            const handleResponse = (response) => {
                const newMessage = {
                    id: messages.length + 1,
                    nickName: 'Casebook AI',
                    message: response.recommendation,
                    type: 'system'
                };
                setMessages(messages => [...messages, newMessage]);
            };

            socket.on('openai-query-response', handleResponse);

            return () => {
                socket.off('openai-query-response', handleResponse);
            };
        }
    }, [socket]);

    return (
        <div id="chat" className="--dark-theme">
            <div className="chat__conversation-board">
                {messages.map((msg, index) => (
                    <div key={index} className={`chat__conversation-board__message-container ${msg.type === 'me' ? 'me' : 'system'}`}>
                        {msg.type === 'system' && (
                            <div className="chat__conversation-board__message__person">
                                <img src="/static/media/scaletransm2.5eb52ef8.png" alt={msg.nickName} style={{ width: '35px', height: '35px', borderRadius: '50%' }} />
                                <span className="chat__conversation-board__message__person__nickname">{msg.nickName}</span>
                            </div>
                        )}
                        <div className="chat__conversation-board__message__context">
                            <span className="chat__conversation-board__message__bubble">{msg.message}</span>
                        </div>
                    </div>
                ))}
            </div>
            <div className="chat__conversation-panel">
                <input
                    className="chat__conversation-panel__input"
                    placeholder="Ask Casebook AI a question about this document..."
                    value={inputValue}
                    onChange={e => setInputValue(e.target.value)}
                    onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                            sendMessage(e.target.value);
                        }
                    }}
                />
                <button className="chat__conversation-panel__button" onClick={() => sendMessage(inputValue)}>
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M2 21l21-9L2 3v7l15 2-15 2v7z" fill="currentColor"/>
                    </svg>
                </button>
            </div>
        </div>
    );
};

export default ChatUI;
