import React, { useState } from 'react';
import '../style/theme-styles/components/Chatstyles.css'; // Ensure the path is correct

const ChatUI = () => {
    const [messages, setMessages] = useState([
        { id: 1, avatarUrl: 'https://randomuser.me/api/portraits/women/44.jpg', nickName: 'Monika Figi', message: 'Hello there!', type: '' },
        { id: 2, avatarUrl: 'https://randomuser.me/api/portraits/men/32.jpg', nickName: 'Thomas Rogh', message: 'What\'s up?', type: '' },
        // { id: 3, avatarUrl: 'https://randomuser.me/api/portraits/men/9.jpg', nickName: 'Dennis Mikle', message: 'Winamp\'s still essential.', type: 'me' }
    ]);

    const sendMessage = (e) => {
        e.preventDefault(); // Prevent the form from causing a page reload
        if (message.trim()) {
            console.log("Send message:", message);
            setMessage(''); // Reset input field after sending
        }
    };

    return (
        <div id="chat" className="--dark-theme">
            <div className="chat__conversation-board">
                {/* Message rendering logic */}
            </div>
            <form className="chat__conversation-panel" onSubmit={sendMessage}>
                <input
                    type="text"
                    className="chat__conversation-panel__input"
                    placeholder="Type a message..."
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                <button type="submit" className="chat__conversation-panel__button">Send</button>
            </form>
        </div>
    );
};

export default ChatUI;
