import React, { useContext } from 'react';
import { Button } from 'reactstrap';
import { UserContext } from '../App';
import { showFileViewer } from '../shared/SidebarItemData/data';

const FileViewerButton = ({ file, sideItemsClick }) => {
    const { state, dispatch } = useContext(UserContext);

    const handleClick = async () => {
        const fileTypeMapping = {
            'pdf': showFileViewer,
            'txt': showFileViewer,
            'docx': showFileViewer
        };

        const fileExtension = file.name.split('.').pop();
        const viewer = fileTypeMapping[fileExtension];

        if (viewer) {
            const updatedFile = {
                ...file,
                url: `${process.env.REACT_APP_BASE_URL}/uploads/${state.auth.userPublicId}/${file.name}`
            };
            dispatch({ type: 'SET_CURR_FILE', payload: updatedFile });
            dispatch({ type: 'TOGGLE_VIEWER', payload: viewer });

            if (typeof sideItemsClick === 'function') {
                sideItemsClick({ showFileViewer: true });
            }
        } else {
            console.error('No viewer available for this file type');
        }
    };

    return (
        <Button color="primary" onClick={handleClick}>
            View PDF
        </Button>
    );
};

export default FileViewerButton;
