import axios from 'axios';
import async from 'async';
import { BASE_URL_DEV } from '../utils';

export async function fetchAndSetFileHighlights(dispatch, state, file, setIsLoading) {
    try {
        console.log("Starting to fetch and set file highlights for file:", file);

        const url = `${BASE_URL_DEV}/highlights-json/${state.auth.userPublicId}/${file.name}`;
        console.log(`Fetching highlights for file: ${file.name} from ${url}`);

        const result = await axios.get(url, {
            headers: {
                'x-access-token': state.auth && state.auth.authToken,
            }
        });

        const fileHighlights = result.data;
        console.log(`Received highlights for file: ${file.name}`, fileHighlights);

        dispatch({ type: "SET_CURR_FILE", payload: file });
        console.log("Dispatched SET_CURR_FILE with payload:", file);

        if (fileHighlights && Array.isArray(fileHighlights.highlights)) {
            dispatch({
                type: "SET_FILE_HIGHLIGHTS",
                payload: {
                    name: file.name,
                    highlights: fileHighlights.highlights
                }
            });
            console.log("Dispatched SET_FILE_HIGHLIGHTS with highlights:", fileHighlights.highlights);
        } else {
            dispatch({
                type: "SET_FILE_HIGHLIGHTS",
                payload: {
                    name: file.name,
                    highlights: [] // Ensure highlights is an array
                }
            });
            console.log("Dispatched SET_FILE_HIGHLIGHTS with empty highlights array");
        }

        const filesResult = await axios.get(`${BASE_URL_DEV}/get/files`, {
            headers: {
                "x-access-token": state.auth && state.auth.authToken,
            },
        });

        const filesResponse = filesResult.data && filesResult.data.files;
        console.log("Fetched files response:", filesResponse);

        if (filesResponse && filesResponse.length > 0) {
            const graphResult = await axios.get(`${BASE_URL_DEV}/get-graphdata`, {
                headers: {
                    "x-access-token": state.auth && state.auth.authToken,
                },
            });

            const allgraphs = graphResult.data;
            console.log("Fetched graph data:", allgraphs);

            let Obj = {};
            let _id = "";

            allgraphs.graphdata.forEach(x => {
                _id = x.fileName;
                Obj[_id] = x.links.filter(e => e.source === "CITATION" || e.source === "PROVISION");
            });

            const fileNew = filesResponse.map(element => {
                return {
                    ...element,
                    CITATION: Obj[element.name] && Obj[element.name].length > 0 ? Obj[element.name].filter(e => e.source === "CITATION") : ["N/A"],
                    PROVISION: Obj[element.name] && Obj[element.name].length > 0 ? Obj[element.name].filter(e => e.source === "PROVISION") : ["N/A"]
                };
            });

            console.log("New file data with citations and provisions:", fileNew);

            setIsLoading(false);
            dispatch({ type: "ADD_FILE", payload: fileNew });
            dispatch({ type: "SET_MODAL", payload: true });
        }
    } catch (error) {
        console.log("General error during fetching and setting file highlights:", error);
        setIsLoading(false);
    }
}
